var decreaseButtons=document.getElementsByClassName("decreaseButton"),increaseButtons=document.getElementsByClassName("increaseButton"),numberElements=document.getElementsByClassName("number");function attachEventHandlers(){for(var e=0;e<decreaseButtons.length;e++)decreaseButtons[e].addEventListener("click",decreaseNumber);for(e=0;e<increaseButtons.length;e++)increaseButtons[e].addEventListener("click",increaseNumber)}function decreaseNumber(){var e=this.parentNode.getElementsByClassName("number")[0],t=parseInt(e.textContent);t>0&&(e.textContent=t-1)}function increaseNumber(){var e=this.parentNode.getElementsByClassName("number")[0],t=parseInt(e.textContent);e.textContent=t+1}attachEventHandlers();